create table superheroes( id_superheroe integer auto_increment not null, nom_superheroe varchar(50) not null, habilidad varchar(50) not null, capa boolean, genero char(1) not null, primary key(id_superheroe) ); 
create table superpoderes( id_superpoder integer auto_increment not null, nom_poder varchar(50) not null, danio integer(200) not null, potencia integer(200) not null, id_superheroe integer not null, primary key(id_superpoder), foreign key fk_superpoder_superheroe(id_superheroe) references superheroes(id_superheroe) ); 
create table villanos(id_villano integer auto_increment not null, nom_villano varchar(50) not null, habilidad varchar(50), capa boolean, genero char(1) not null, id_superheroe integer not null, primary key(id_villano), foreign key fk_villano_superheroe(id_superheroe) references superheroes(id_superheroe) ); 
create table marcas(id_marca integer auto_increment not null, nom_marca varchar(50) not null, anio_creacion integer(11) not null, pelicula boolean,  id_villano integer not null, primary key(id_marca), foreign key fk_marca_villano(id_villano) references villanos(id_villano) );